<?php
 define('NetteCacheDirectory',realpath(__DIR__.'/../../temp/cache/'));use Nette\Caching\Cache,Nette\Caching\Storages\FileJournal,Nette\Caching\Storages\FileStorage;class CacheHelpers{public static function load($a){$b=new FileJournal(NetteCacheDirectory.'/..');$c=new FileStorage(NetteCacheDirectory,$b);$d=new Cache($c,$a);return $d;}public static function clear($e){$d=self::load($e);$d->clean(array(Cache::TAGS=>$e));unset($d);unset($e);}public static function parse($f){if($f===false){return array();}if(get_class($f)=='Nette\Database\Table\ActiveRow' or get_class($f)=='ArrayHash'){return self::ParseSingle($f);}$g=array();foreach($f as $h){$i=array();foreach($h as $j=>$k){$i[$j]=$k;}$g[]=$i;unset($i);}unset($f);return $g;}public static function parseSingle($l){$g=array();foreach($l as $j=>$k){$g[$j]=$k;}return $g;}}
